class PublicContentBundles {

    /**
     * Get Content Pages.
     * Get all content pages.
     * @param {Object} options - List of resource collection options.
     * @returns {Promise} - A promise that resolves with the content pages data.
     */
    getContentBundles(options = null) {
        return this.client.raw('get', '/content/content-bundles', options);
    }

    /**
     * Get Content Page.
     * Get a single content page.
     * @param {string} slug - It's the string identifier generated by Mints.
     * @param {Object} options - List of resource collection options.
     * @returns {Promise} - A promise that resolves with the content page data.
     * @deprecated This method is deprecated and will be removed in the future, use getContentBundle instead.
     */
    getContentPage(slug, options = null) {
        console.warn('[DEPRECATED] The getContentPage method is deprecated and will be removed in the future, use getContentBundle instead');
        return this.client.raw('get', `/content/content-pages/${slug}`, options);
    }

    /**
     * Get Content Bundle.
     * Get a single content bundle.
     * @param {string} slug - It's the string identifier generated by Mints.
     * @param {Object} options - List of resource collection options.
     * @returns {Promise} - A promise that resolves with the content bundle data.
     */
    getContentBundle(slug, options = null) {
        return this.client.raw('get', `/content/content-bundles/${slug}`, options);
    }
}

export default PublicContentBundles;